import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '400px',
    height: '400px',
};

const center = {
    lat: -3.745,
    lng: -38.523,
};

function NewMap({ apiKey }) { // Receive apiKey as a prop
    const { isLoaded, loadError } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: apiKey, // Use the prop
    });

    const [map, setMap] = useState(null);
    const [apiData, setApiData] = useState(null);

    useEffect(() => {
        // ... your fetchData logic
    }, [isLoaded]);

    const onLoad = useCallback(function callback(map) {
        // ... your onLoad logic
    }, []);

    const onUnmount = useCallback(function callback(map) {
        // ... your onUnmount logic
    }, []);

    const updateMapWithApiData = useCallback(() => {
        // ... your updateMapWithApiData logic
    }, [map, apiData]);

    useEffect(() => {
        updateMapWithApiData();
    }, [updateMapWithApiData]);

    if (loadError) {
        return <div>Error loading Google Maps</div>;
    }

    return isLoaded ? (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={center}
            zoom={10}
            onLoad={onLoad}
            onUnmount={onUnmount}
        >
            {/* Child components */}
        </GoogleMap>
    ) : (
        <div>Loading Google Maps...</div>
    );
}

function ParentComponent() {
    const [googleMapsApiKey, setGoogleMapsApiKey] = useState('YOUR_ACTUAL_GOOGLE_MAPS_API_KEY'); // Or fetch it

    // ... logic to get the API key ...

    return (
        <div>
            {googleMapsApiKey && <NewMap apiKey={googleMapsApiKey} />}
        </div>
    );
}

export default React.memo(NewMap);
