import { useState } from "react";
import TaskCard from "./TaskCard";

const Column = ({ title, tasks, onDrop, IndexVal, onEdit, onDelete }) => {
    const [draggedTaskId, setDraggedTaskId] = useState(null);

    const handleDragStart = (e, id) => {
        setDraggedTaskId(id);
    };

    const handleDragOver = (e, id) => {
        e.preventDefault();
    };

    const handleDropOnTask = (e, dropTargetId) => {
        e.preventDefault();
        if (draggedTaskId !== null && draggedTaskId !== dropTargetId) {
            onDrop(draggedTaskId, dropTargetId, title); // Pass destination task too
        }
        setDraggedTaskId(null);
    };

    const handleDropOnEmpty = (e) => {
        e.preventDefault();
        if (draggedTaskId !== null) {
            onDrop(draggedTaskId, null, title); // Drop at end
        }
        setDraggedTaskId(null);
    };

    const filteredTasks = tasks.filter((t) => t.status === title);

    return (
        <div
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDropOnEmpty}
            className="bg-gray-100 p-4 rounded shadow min-h-[300px] group relative"
        >
            <h2 className={`text-xl font-semibold mb-2 ${IndexVal === 0 ? "text-red-500" :
                    IndexVal === 1 ? "text-yellow-500" :
                        "text-green-500"
                }`}>
                {title}
            </h2>

            {filteredTasks.map((task) => (
                <TaskCard
                    key={task.id}
                    task={task}
                    onEdit={onEdit}
                    onDelete={onDelete}
                    onDragStart={handleDragStart}
                    onDragOver={handleDragOver}
                    onDrop={handleDropOnTask}
                />
            ))}
        </div>
    );
};

export default Column;
