import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { useContext } from "react";
import NoteContext from "../Ncontext/NoteContext";
import TaskFormModal from "./TaskFormModal";
import { useDispatch } from "react-redux";
import { addTask, updateTask } from "../features/tasks/taskSlice";
const TaskCard = ({ task }) => {
    const dispatch = useDispatch();
    const handleDragStart = (e) => {
        e.dataTransfer.setData("text/plain", task.id);
    };
    const context = useContext(NoteContext);
    const { showModal, setShowModal, editTask, setEditTask } = context;
    const handleShowModal = () => {
        setShowModal(true);
    };
    const handleDeleteTask = () => {
        alert("Delete task with ID:", task.id);
    }
    return (
        <div
            draggable
            onDragStart={handleDragStart}
            className="flex justify-between bg-white p-3 rounded shadow mb-2 cursor-grab"
        >
            <div className="flex flex-col justify-between ">
                <h3 className="font-bold text-lg">{task.title}</h3>
                <p className="text-sm text-gray-600">{task.description}</p>

            </div>
            <div className="flex gap-4 items-center justify-between mb-2">
                <div className="flex gap-4 items-center justify-between opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <div onClick={handleShowModal} className="h-7 w-7 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faEdit} className="cursor-pointer text-[16px] text-blue-500" />
                    </div>
                    <div onClick={handleDeleteTask} className="h-7 w-7 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faTrash} className="cursor-pointer text-[16px] text-red-500" />
                    </div>
                </div>
            </div>
            {/* {showModal && (
                <TaskFormModal
                    onClose={() => setShowModal(false)}
                    onAdd={task => dispatch(addTask(task))}
                />
            )} */}
            {showModal && (<TaskFormModal
                onClose={() => {
                    setShowModal(false);
                    setEditTask(null);
                }}
                onAdd={(task) => dispatch(addTask(task))}
                onUpdate={(updatedTask) => dispatch(updateTask(updatedTask))}
                initialData={editTask}
            />
            )}
        </div>
    );
}
export default TaskCard
