import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
import { useContext } from "react";
const TaskCard = ({ task }) => {
    const handleDragStart = (e) => {
        e.dataTransfer.setData("text/plain", task.id);
    };
useContext(NoteContext);
    return (
        <div
            draggable
            onDragStart={handleDragStart}
            className="flex justify-between bg-white p-3 rounded shadow mb-2 cursor-grab"
        >
            <div className="flex flex-col justify-between ">
                <h3 className="font-bold text-lg">{task.title}</h3>
                <p className="text-sm text-gray-600">{task.description}</p>

            </div>
            <div className="flex gap-4 items-center justify-between mb-2">
                <div className="flex gap-4 items-center justify-between opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <div onClick={() => {
                        alert("Edit Task");
                    }} className="h-7 w-7 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faEdit} className="cursor-pointer text-[16px] text-blue-500" />
                    </div>
                    <div className="h-7 w-7 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faTrash} className="cursor-pointer text-[16px] text-red-500" />
                    </div>
                </div>
            </div>
            {showModal && (
                <TaskFormModal
                    onClose={() => setShowModal(false)}
                    onAdd={task => dispatch(addTask(task))}
                />
            )}
        </div>
    );
}
export default TaskCard
