import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash } from "@fortawesome/free-solid-svg-icons";
const TaskCard = ({ task }) => {
    const handleDragStart = (e) => {
        e.dataTransfer.setData("text/plain", task.id);
    };

    return (
        <div
            draggable
            onDragStart={handleDragStart}
            className="flex bg-white p-3 rounded shadow mb-2 cursor-grab"
        >
            <div className="flex flex-col items-center justify-between mb-2">
                <h3 className="font-bold text-lg">{task.title}</h3>
                <p className="text-sm text-gray-600">{task.description}</p>

            </div>
            <div className="flex  items-center justify-between mb-2">

                <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <div className="h-8 w-8 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faEdit} className="cursor-pointer text-[20px] text-white" />
                    </div>
                    <div className="h-8 w-8 bg-gray-950 flex items-center justify-center text-white rounded-full">
                        <FontAwesomeIcon icon={faTrash} className="cursor-pointer text-[20px] text-red-500" />
                    </div>
                </div>
            </div>
        </div>
    );
}
export default TaskCard
