import React, { useState, useEffect, useCallback } from 'react';
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '400px',
    height: '400px',
};

const center = {
    lat: -3.745,
    lng: -38.523,
};

function NewMap() {
    const [apiData, setApiData] = useState(null); // State to store data from your API

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: apiData, // Replace with your actual Google Maps API key
    });

    const [map, setMap] = useState(null);

    const fetchData = useCallback(async () => {
        try {
            const response = await fetch('https://secret-key-services-92086589662.us-central1.run.app/SecretKeys', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    "user": "Admin",
                    "password": "GenAI@7070",
                    "token": "GenAI-SecretKeys"
                })
            });

            if (response.ok) {
                const data = await response.json();
                setApiData(data, data);
                console.log("Data from your API:", data);
            } else {
                console.error('Failed to fetch data from your API:', response.status);
            }
        } catch (error) {
            console.error('Error fetching data from your API:', error);
        }
    }, []); // Removed isLoaded from dependency array

    useEffect(() => {
        if (isLoaded) {
            fetchData();
        }
    }, [isLoaded, fetchData]); // Added fetchData to dependency array

    const onLoad = useCallback(function callback(map) {
        const bounds = new window.google.maps.LatLngBounds(center);
        map.fitBounds(bounds);
        setMap(map);
    }, []);

    const onUnmount = useCallback(function callback(map) {
        setMap(null);
    }, []);

    // Function to update the map based on apiData
    const updateMapWithApiData = useCallback(() => {
        if (map && apiData) {
            // Example: If your API returns location data, you can add markers
            // Assuming your API data has 'latitude' and 'longitude' properties
            if (apiData.some(item => item.latitude && item.longitude)) {
                apiData.forEach(item => {
                    new window.google.maps.Marker({
                        position: { lat: item.latitude, lng: item.longitude },
                        map: map,
                        title: item.title || 'API Data Point',
                    });
                });
            }
            // Add other map updates based on your API data here
        }
    }, [map, apiData]);

    useEffect(() => {
        updateMapWithApiData();
    }, [updateMapWithApiData]);

    return isLoaded ? (
        <GoogleMap
            mapContainerStyle={containerStyle}
            center={center}
            zoom={10}
            onLoad={onLoad}
            onUnmount={onUnmount}
        >
            {/* Child components, such as markers, info windows, etc. */}
            <></>
        </GoogleMap>
    ) : (
        <></>
    );
}

export default React.memo(NewMap);
