import TaskCard from "./TaskCard";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";

const Column = ({ title, tasks, onDrop, IndexVal, }) => {
    const handleDrop = (e) => {
        const taskId = e.dataTransfer.getData("text/plain");
        onDrop(Number(taskId), title);
    };
const [mouseOver, setMouseOver] = useState(false);
const handleMouseOver =()=>{
    setMouseOver(true);
}
    return (
        <div
            onDragOver={(e) => e.preventDefault()}
            onDrop={handleDrop}
            className="bg-gray-100 p-4 rounded shadow min-h-[300px]"
        >
            <h2
                className={`text-xl font-semibold mb-2 ${IndexVal === 0
                    ? "text-red-500"
                    : IndexVal === 1
                        ? "text-yellow-500"
                        : "text-green-500"
                    }`}
            >
                {title}
            </h2>
        { mouseOver &&   <div className="h-10 w-20 border-4 border-red-500 bg-gray-950 flex items-center justify-center text-white">
                <FontAwesomeIcon icon={faEdit} className="cursor-pointer text-[20px] text-white" />
            </div>}
            {tasks
                .filter((t) => t.status === title)
                .map((task) => (
                    <TaskCard
                        key={task.id}
                        task={task}

                    />


                ))}
        </div>
    );
};

export default Column;
