import { useEffect, useState } from "react";
import Column from "./Column";
import TaskFormModal from "./TaskFormModal";
import { loadTasks, addTask, changeTaskStatus } from "../controllers/taskController";

const Board =()=> {
    const [tasks, setTasks] = useState([]);
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        loadTasks(setTasks);
    }, []);

    const handleDrop = (taskId, newStatus) => {
        changeTaskStatus(taskId, newStatus, tasks, setTasks);
    };

    return (
        <div className="p-4">
            <div className="flex justify-between items-center mb-4">
                <h1 className="text-2xl font-bold">Task Board</h1>
                <button onClick={() => setShowModal(true)} className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                    Add New Task
                </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {["To Do", "In Progress", "Done"].map(status => (
                    <Column key={status} title={status} tasks={tasks} onDrop={handleDrop} />
                ))}
            </div>
            {showModal && (
                <TaskFormModal
                    onClose={() => setShowModal(false)}
                    onAdd={task => addTask(task, setTasks)}
                />
            )}
        </div>
    );
}
export default Board
