import { useState, useEffect } from "react";

const TaskFormModal = ({ onClose, onAdd, onUpdate, initialData }) => {
    const [form, setForm] = useState({ title: "", description: "", status: "To Do" });

    useEffect(() => {
        if (initialData) {
            setForm({
                title: initialData.title || "",
                description: initialData.description || "",
                status: initialData.status || "To Do",
            });
        }
    }, [initialData]);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!form.title.trim()) return alert("Title is required");

        const task = {
            ...form,
            id: initialData?.id || Date.now().toString(),
        };

        if (initialData) {
            onUpdate(task);
        } else {
            onAdd(task);
        }

        onClose();
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
                <h2 className="text-xl font-bold mb-4">{initialData ? "Edit Task" : "Add Task"}</h2>
                <form onSubmit={handleSubmit} className="space-y-4">
                    <input
                        type="text"
                        placeholder="Title"
                        value={form.title}
                        onChange={e => setForm({ ...form, title: e.target.value })}
                        className="w-full border p-2 rounded"
                        required
                    />
                    <textarea
                        placeholder="Description"
                        value={form.description}
                        onChange={e => setForm({ ...form, description: e.target.value })}
                        className="w-full border p-2 rounded"
                    />
                    <select
                        value={form.status}
                        onChange={e => setForm({ ...form, status: e.target.value })}
                        className="w-full border p-2 rounded"
                    >
                        {["To Do", "In Progress", "Done"].map(s => (
                            <option key={s} value={s}>{s}</option>
                        ))}
                    </select>
                    <div className="flex justify-end gap-2">
                        <button type="button" onClick={onClose} className="text-gray-600 border-2 px-4 rounded">
                            Cancel
                        </button>
                        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">
                            {initialData ? "Update" : "Add"}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default TaskFormModal;
