import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { fetchTasksAPI, addTaskAPI, updateTaskAPI } from "./taskAPI";

export const fetchTasks = createAsyncThunk("tasks/fetch", async () => {
  return await fetchTasksAPI();
});

export const addTask = createAsyncThunk("tasks/add", async (task) => {
  const newTask = await addTaskAPI(task);
  newTask.status = task.status;
  return newTask;
});

export const updateTask = createAsyncThunk("tasks/update", async (task) => {
  return await updateTaskAPI(task);
});

const taskSlice = createSlice({
  name: "tasks",
  initialState: {
    items: [],
    loading: false,
    error: null,
  },
  reducers: {
    deleteTask: (state, action) => {
      return state.filter((task) => task.id !== action.payload);
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchTasks.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchTasks.fulfilled, (state, action) => {
        state.items = action.payload;
        state.loading = false;
      })
      .addCase(addTask.fulfilled, (state, action) => {
        state.items.push(action.payload);
      })
      .addCase(updateTask.fulfilled, (state, action) => {
        const index = state.items.findIndex((t) => t.id === action.payload.id);
        if (index !== -1) state.items[index] = action.payload;
      });
  },
});

export default taskSlice.reducer;
