import { deleteTask, updateTask } from "../features/tasks/taskSlice"; // Add delete action

const [editTaskData, setEditTaskData] = useState(null); // For editing

const handleEdit = (task) => {
    setEditTaskData(task); // Open form prefilled
};

const handleDelete = (id) => {
    dispatch(deleteTask(id));
};
