// Define API configuration first
const apiConfig = {
  request: {
    get: "GET",
    post: "POST",
    put: "PUT",
    patch: "PATCH",
    delete: "DELETE",
  },
  CountryCodes_API: {
    end_point:
      "https://mobile-services-92086589662.us-central1.run.app/CountryCodes",
    body: {
      user: "Admin",
      password: "GenAI@7070",
      token: "GenAI-CountryCodes",
    },
  },
  Query_API: {
    end_point:
      "https://query-services-92086589662.us-central1.run.app/UserQuery",
    body: {
      user: "Admin",
      password: "GenAI@7070",
      token: "GenAI-QueryData",
    },
  },
};

// API Request Function
export const apiRequest = async (apiName, method, data = {}) => {
  if (!apiConfig[apiName]) {
    console.error(`API configuration for ${apiName} not found`);
    return null;
  }

  const { end_point, body } = apiConfig[apiName];

  const requestOptions = {
    method: apiConfig.request[method.toLowerCase()],
    headers: {
      "Content-Type": "application/json",
    },
    body:
      method.toLowerCase() !== "get"
        ? JSON.stringify({ ...body, ...data })
        : null,
  };

  try {
    const response = await fetch(end_point, requestOptions);
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    return await response.json();
  } catch (error) {
    console.error("API Request Failed:", error);
    return null;
  }
};
