import axios from "axios";

axios
const API_URL =
  "https://mobile-services-92086589662.us-central1.run.app/CountryCodes";

export const fetchCountryCodes = async () => {
  try {
    const response = await fetch(API_URL, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        // ❌ "Access-Control-Allow-Origin" is NOT needed here (must be set by the server)
      },
      credentials: "include", // Same as `withCredentials: true` in Axios
      body: JSON.stringify({
        user: "Admin",
        password: "GenAI@7070",
        token: "GenAI-CountryCodes",
      }),
    });

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Error fetching country codes:", error);
    return [];
  }
};

const API_URLs =
  "https://query-services-92086589662.us-central1.run.app/UserQuery";

export const submitUserQuery = async (name, email, query) => {
  try {
    const response = await axios.post(
      API_URLs,
      {
        user: "Admin",
        password: "GenAI@7070",
        token: "GenAI-QueryData",
        data: { name, email, query },
      },
      {
        headers: {
          "Content-Type": "application/json",
        },
        withCredentials: true,
      }
    );

    return response.data;
  } catch (error) {
    console.error("Error submitting query:", error);
    return { success: false, message: "Failed to submit query" };
  }
};
