import { useContext, useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchTasks, addTask, updateTask } from "../features/tasks/taskSlice";
import Column from "./Column";
import TaskFormModal from "./TaskFormModal";
import NoteContext from "../Ncontext/NoteContext";

const Board = () => {
    const dispatch = useDispatch();
    const tasks = useSelector(state => state.tasks.items);
    const context = useContext(NoteContext);
    const { showModal, setShowModal } = context;
    useEffect(() => {
        dispatch(fetchTasks());
    }, [dispatch]);

    const handleDrop = (taskId, newStatus) => {
        const task = tasks.find(t => t.id === taskId);
        if (task && task.status !== newStatus) {
            dispatch(updateTask({ ...task, status: newStatus }));
        }
    };

    return (
        <div className="p-4">
            <div className="flex justify-between items-center mb-4 flex-col xs:flex-row">
                <h1 className="text-2xl font-bold pb-2 xs:pb-auto">Kanban Board</h1>
                <button onClick={() => setShowModal(true)} className="bg-blue-600 text-white px-4 py-2 rounded">
                    Add New Task
                </button>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 ">
                {["To Do", "In Progress", "Done"].map((status, index) => (
                    <Column key={status} title={status} tasks={tasks} IndexVal={index} onDrop={handleDrop} />
                ))}
            </div>
            {showModal && (
                <TaskFormModal
                    onClose={() => setShowModal(false)}
                    onAdd={task => dispatch(addTask(task))}
                />
            )}
        </div>
    );
}

export default Board
