import React, { useState } from "react";
import axios from "axios";

const OTPGenerator = () => {
    const [mobileNumber, setMobileNumber] = useState("");
    const [responseMessage, setResponseMessage] = useState("");

    const sendOTP = async () => {
        try {
            const response = await axios.post(
                "https://sms-services-92086589662.us-central1.run.app/SMSGenerateOTP",
                {
                    user: "Admin",
                    password: "GenAI@7070",
                    token: "GenAI-MobileOTP",
                    data: {
                        country_code: "+91",
                        mobile_number: mobileNumber,
                    },
                },
                {
                    headers: {
                        "Content-Type": "application/json",
                    },
                }
            );

            setResponseMessage(`Success: ${response.data.message}`);
        } catch (error) {
            setResponseMessage(`Error: ${error.response?.data?.message || error.message}`);
        }
    };

    return (
        <div className="p-4 max-w-sm mx-auto bg-white shadow-lg rounded-lg">
            <h2 className="text-xl font-bold mb-4">OTP Generator</h2>
            <input
                type="text"
                placeholder="Enter mobile number"
                className="w-full p-2 border rounded"
                value={mobileNumber}
                onChange={(e) => setMobileNumber(e.target.value)}
            />
            <button
                onClick={sendOTP}
                className="w-full mt-3 bg-blue-500 text-white py-2 rounded hover:bg-blue-600"
            >
                Send OTP
            </button>
            {responseMessage && <p className="mt-3 text-center">{responseMessage}</p>}
        </div>
    );
};

export default OTPGenerator;
