import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-solid-svg-icons";

export default function TaskCard({ task }) {
  const [hover, setHover] = useState(false);

  return (
    <div
      draggable
      onDragStart={(e) => e.dataTransfer.setData("text/plain", task.id)}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      className="relative bg-white p-3 rounded shadow mb-2 cursor-grab group"
    >
      <h3 className="font-bold">{task.title}</h3>
      <p className="text-sm text-gray-600">{task.description}</p>

      {hover && (
        <div
          onClick={() => alert(`Edit clicked for task: ${task.title}`)}
          className="absolute top-2 right-2 text-blue-600 cursor-pointer"
        >
          <FontAwesomeIcon icon={faEdit} className="text-[18px]" />
        </div>
      )}
    </div>
  );
}
