import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
    const [formData, setFormData] = useState({
        fullname: '',
        username: '',
        email: '',
        userpassword: '',
        country: 'India',
        countrycode: '+91',
        phone: '',
        address: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };
    console.log({ formData: formData });

    const handleSubmit = async (e) => {
        e.preventDefault();

        const payload = {
            user: 'Admin',
            password: 'GenAI@7070',
            token: 'GenAI-SignUpData',
            data: formData
        };

        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        };

        try {
            const response = await axios.post(
                'https://signup-services-92086589662.us-central1.run.app/SignUp',
                payload,
                config
            );

            console.log(response.data);
            alert('Sign Up Successful!');
        } catch (error) {
            console.error('Error:', error.response ? error.response.data : error.message);
            alert('Sign Up Failed. Please check your credentials and try again.');
        }
    };



    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-900">
            <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-xl shadow-md w-96 space-y-4">
                <h2 className="text-2xl font-bold text-center text-white">Create a New Account</h2>

                <div className="flex items-center space-x-2">
                    <input name="fullname" type="text" placeholder="Full Name" value={formData.fullname} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <div className="flex items-center space-x-2">

                    <input name="username" type="text" placeholder="Username" value={formData.username} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <div className="flex items-center space-x-2">

                    <input name="email" type="email" placeholder="Email" value={formData.email} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <div className="flex items-center space-x-2">

                    <input name="userpassword" type="password" placeholder="Password" value={formData.userpassword} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <div className="flex items-center space-x-2">
                    <input name="countrycode" type="text" value={formData.countrycode} disabled className="w-12 p-2 rounded bg-gray-700 text-white" />

                    <input name="phone" type="text" placeholder="Mobile Number" value={formData.phone} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <div className="flex items-center space-x-2">

                    <input name="address" type="text" placeholder="Address" value={formData.address} onChange={handleChange} className="w-full p-2 rounded" />
                </div>

                <button type="submit" className="w-full bg-teal-500 text-white p-2 mt-4 rounded">Sign Up</button>
            </form>
        </div>
    );
};

export default Signup;
