import TaskCard from "./TaskCard";

const Column = ({ title, tasks, onDrop, IndexVal }) => {
    const handleDrop = (e) => {
        const taskId = e.dataTransfer.getData("text/plain");
        onDrop(Number(taskId), title);
    };

    return (
        <div
            onDragOver={e => e.preventDefault()}
            onDrop={handleDrop}
            className="bg-gray-100 p-4 rounded shadow min-h-[300px]"
        >

            <h2 className={`text-xl font-semibold mb-2 ${IndexVal == 0 ? "text-red-500" : IndexVal == 1 ? "text-yellow-500" : "text-green-500"}`}>{title}</h2>

            p{tasks.filter(t => t.status === title).map(task => (
                <TaskCard key={task.id} task={task} />
            ))}
        </div>
    );
}
export default Column
