import React from "react";
import trophy from '../Assets/trophy.png'
import acount_alert from '../Assets/shipping.png'
import guarantee from '../Assets/guarantee.png'
import customer_supp from '../Assets/customer-support.png'

const InfoCard = () => {
    const info = [
        { title: "High Quality", description: "Crafted from top materials", icon: trophy },
        { title: "Warranty Protection", description: "Over 2 years", icon: guarantee },
        { title: "Free Shipping", description: "Order over 150$", icon: acount_alert },
        { title: "24/7 Support", description: "Dedicated support", icon: customer_supp },
    ];

    return (
        <div className="bg-[#FAF3EA] h-[200px] flex  justify-between items-center ">
            <div className="container mx-auto flex flex-wrap">
                {info.map((item, index) => (
                    <div
                        key={index}
                        className="w-1/2 md:w-1/4 px-auto  flex  gap-2 items-start"
                    >
                        <div className="w-[60px] h-[60px] flex justify-center items-center mb-3">
                            <img src={item.icon} alt="icon" />
                        </div>
                        <div className="flex flex-col">
                            <h3 className="font-bold text-lg mb-1">{item.title}</h3>
                            <p className="text-[#898989] text-sm">{item.description}</p>

                        </div>
                    </div>
                ))}
            </div>
        </div>


    );
};

export default InfoCard;
