import React, { useState, useCallback } from 'react';

const ChildDisplay = ({ message }) => {
    console.log('ChildDisplay ');
    return <p>Message from Parent: {message}</p>;
}

function ParentCallbackHookExample() {
    const [text, setText] = useState('');

    const handleSendMessage = useCallback(() => {
        console.warn(`Sending message: ${text}`);
    }, [text]);

    const handleChange = (event) => {
        setText(event.target.value);
    };

    console.log('ParentMessagingComponent ');

    return (
        <div>
            <input className='border-2 ' type="text" value={text} onChange={handleChange} />
            <button className='text-2xl border-2 border-red-500' onClick={handleSendMessage}>Send Message</button>
            <ChildDisplay message={`Current text: ${text}`} />
        </div>
    );
}

export default ParentCallbackHookExample;
